const data = $('Get List of Replies').first().json.messages.sort( function compare( a, b ) {
  if ( a.ts < b.ts ){
    return 1;
  }
  if ( a.ts > b.ts ){
    return -1;
  }
  return 0;
} ).slice(0, 4) 

let replies = '';



for (const item of data) {
let user_name="bot"
  for (const user of $('Get Users').all()) {
   if(user.json.id === item.user){
     user_name = user.json.real_name
     break;
   }
  }
  replies += DateTime.fromSeconds(parseFloat(item.ts)).toFormat('yyyyMMdd hh:mm') + "\n" + user_name+ ": " + item.text + "\n" + "\n" 
}

return [{"replies": replies,"id": $('Loop Over Items').item.json.id }];
